const fs = require("fs");

function dir(pathName) {

    return new Promise((resolve, reject) => {

        fs.readdir(pathName, (error, pathnames) => {

            if (error) {

                throw new Error(error);

            } else {
                resolve(pathnames)
            }

        });
    });
}


dir(__dirname).then(v => {
    v.forEach(v=> {
        console.log(`"${v}": resolve(__dirname, "./src/node_modules/${v}"),`)
    })

    console.log(v.join("\",\""));
})
